import React, { Component } from "react";
import axios from 'axios';

export default class Signin extends Component {

    constructor(props) {
        super(props);
    this.state = {
          email: '',
          password: ''
        }
      }

    // componentDidMount() {
    //     axios.post(`https://localhost:3000/user/login`, {email: email, password: password})
    //         .then(res => {
    //         console.log(res.body);
    //     })
    // }

    onChangeEmail(e) {
      /*  this.setState({
          email: e.target.value
        })*/
      }

    onChangePassword(e) {
        this.setState({
          password: e.target.value
        })
      }

    onSubmit(e){
        e.preventDefault();

    const login = {
      email: this.state.email,
      password: this.state.password
    }

    console.log(login);

    axios.post('http://localhost:3000/user/login', login)
      .then(res => console.log(res.data));
    }

    render() {
        return (
            <form onSubmit={this.onSubmit}>
                <h3>Sign In</h3>

                <div className="form-group">
                    <label>Email address</label>
                    <input type="email" className="form-control" placeholder="Enter email" value={this.state.email} onChange={this.onChangeEmail}/>
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" className="form-control" placeholder="Enter password" value={this.state.password} onChange={this.onChangePassword}/>
                </div>

                <div className="form-group">
                    <div className="custom-control custom-checkbox">
                        <input type="checkbox" className="custom-control-input" id="customCheck1" />
                        <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                    </div>
                </div>
                <br/>
                <button type="submit" className="btn btn-primary btn-block">Submit</button>
                <br/>
                <p className="forgot-password text-right">
                    Forgot <a href="#">password?</a>
                </p>
            </form>
        );
    }
}
